e&#34;&gt;        System.out.println(hero.name + &lt;span class=&#34;string&#34;&gt;&amp;quot;的经济现在是&amp;quot;&lt;/span&gt; + hero.money);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;comment&#34;&gt;//开始创建装备类&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        System.out.println(&lt;span class=&#34;string&#34;&gt;&amp;quot;购买装备前：&amp;quot;&lt;/span&gt;);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        System.out.println(hero.name + &lt;span class=&#34;string&#34;&gt;&amp;quot;当前的属性&amp;quot;&lt;/span&gt; + &lt;span class=&#34;string&#34;&gt;&amp;quot;生命值：&amp;quot;&lt;/span&gt; + hero.blood + &lt;span class=&#34;string&#34;&gt;&amp;quot;物理攻击：&amp;quot;&lt;/span&gt; + hero.phyattack + &lt;span class=&#34;string&#34;&gt;&amp;quot;法术攻击：&amp;quot;&lt;/span&gt; + hero.Magicattavk +&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                &lt;span class=&#34;string&#34;&gt;&amp;quot; 物理防御：&amp;quot;&lt;/span&gt; + hero.phyfangyu + &lt;span class=&#34;string&#34;&gt;&amp;quot;移动速度：&amp;quot;&lt;/span&gt; + hero.move);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;comment&#34;&gt;//判断经济是否购买装备&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;keyword&#34;&gt;if&lt;/span&gt; (hero.money &amp;gt;= &lt;span class=&#34;number&#34;&gt;200&lt;/span&gt;) &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            Equipment equipment = &lt;span class=&#34;keyword&#34;&gt;new&lt;/span&gt; Equipment();&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            System.out.println(hero.name + &lt;span class=&#34;string&#34;&gt;&amp;quot;购买了&amp;quot;&lt;/span&gt; + equipment.name);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            hero.phyfangyu += equipment.getPhyattack();&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            hero.move += equipment.getMove();&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            hero.money = hero.money - &lt;span class=&#34;number&#34;&gt;200&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;keyword&#34;&gt;if&lt;/span&gt; (hero.money &amp;gt;= &lt;span class=&#34;number&#34;&gt;1200&lt;/span&gt;) &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            The_armor armor = &lt;span class=&#34;keyword&#34;&gt;new&lt;/span&gt; The_armor();&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            System.out.println(hero.name + &lt;span class=&#34;string&#34;&gt;&amp;quot;购买了&amp;quot;&lt;/span&gt; + armor.name);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            hero.phyattack += armor.getPhyattack(); &lt;span class=&#34;comment&#34;&gt;//攻击力+&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            hero.phyfangyu += armor.getPhyfanyu();  &lt;span class=&#34;comment&#34;&gt;//防御力+&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            hero.money = hero.money - &lt;span class=&#34;number&#34;&gt;1200&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;keyword&#34;&gt;if&lt;/span&gt; (hero.money &amp;gt;= &lt;span class=&#34;number&#34;&gt;2000&lt;/span&gt;) &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            Shadow_tomahawk tomahawk = &lt;span class=&#34;keyword&#34;&gt;new&lt;/span&gt; Shadow_tomahawk();&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            System.out.println(hero.name + &lt;span class=&#34;string&#34;&gt;&amp;quot;购买了&amp;quot;&lt;/span&gt; + tomahawk.name);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            hero.phyattack += tomahawk.getPhyattack(); &lt;span class=&#34;comment&#34;&gt;//攻击力+&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            hero.blood += tomahawk.getBlood();  &lt;span class=&#34;comment&#34;&gt;//生命力+&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            hero.money = hero.money - &lt;span class=&#34;number&#34;&gt;2000&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        System.out.println(&lt;span class=&#34;string&#34;&gt;&amp;quot;购买装备后：&amp;quot;&lt;/span&gt;);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        System.out.println(hero.name + &lt;span class=&#34;string&#34;&gt;&amp;quot;当前的属性&amp;quot;&lt;/span&gt; + &lt;span class=&#34;string&#34;&gt;&amp;quot;生命值：&amp;quot;&lt;/span&gt; + hero.blood + &lt;span class=&#34;string&#34;&gt;&amp;quot;物理攻击：&amp;quot;&lt;/span&gt; + hero.phyattack + &lt;span class=&#34;string&#34;&gt;&amp;quot;法术攻击：&amp;quot;&lt;/span&gt; + hero.Magicattavk +&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                &lt;span class=&#34;string&#34;&gt;&amp;quot; 物理防御：&amp;quot;&lt;/span&gt; + hero.phyfangyu + &lt;span class=&#34;string&#34;&gt;&amp;quot;移动速度：&amp;quot;&lt;/span&gt; + hero.move);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
&lt;p&gt;&lt;img data-src=&#34;https://upimage.alexhchu.com/2021/05/21/ed8f51aaa9677.png&#34; alt=&#34;效果&#34;&gt;&lt;/p&gt;
&lt;p&gt;我基本可以实现简单的创建一个英雄，实现刷野怪，购买装备的简单操作，后序我将实现英雄的三个技能和铭文的实现，感谢支持！&lt;/p&gt;
&lt;h3 id=&#34;码坑&#34;&gt;&lt;a class=&#34;markdownIt-Anchor&#34; href=&#34;#码坑&#34;&gt;#&lt;/a&gt; 码坑&lt;/h3&gt;
